sequence.script:

var deleted = mc.getProperty('uri.var.deleted');
var archived = mc.getProperty('uri.var.archived');
var perPage = mc.getProperty('uri.var.perPage');
var page = mc.getProperty('uri.var.page');
var sort = mc.getProperty('uri.var.sort');
var order = mc.getProperty('uri.var.order');
var urlQuery = '';
if (page != null && page != "") {
   urlQuery += 'page=' + page + '&';
}
if (perPage != null && perPage != "") {
   urlQuery += 'per_page=' + perPage + '&';
}
if (sort != null && sort != "") {
   urlQuery += 'sort=' + sort + '&';
}
if (order != null && order != "") {
   urlQuery += 'order=' + order + '&';
}
// Add a leading '?' and remove the trailing '&'
if (urlQuery != "") {
   urlQuery = '?' + urlQuery.substring(0, urlQuery.length - 1);
   mc.setProperty('uri.var.urlQuery', urlQuery);
}
// Set the endpoint leaf based on whether the user wants to retrieve only the deleted patients or archived patients or all.
if (deleted == 'true' || deleted == 1) {
   mc.setProperty('uri.var.endpointLeaf', '/patients/deleted');
} else if (archived == 'true' || archived == 1) {
   mc.setProperty('uri.var.endpointLeaf', '/patients/archived');
} else {
   mc.setProperty('uri.var.endpointLeaf', '/patients');
}
