sequence.script:

// Generates the SOAP Body with optional parameters
var properties = mc.getProperty('uri.var.properties');
var openEventsFilter = mc.getProperty('uri.var.openEventsFilter');
var listOpenEventsProperties = <RetrieveRequest xmlns="http://exacttarget.com/wsdl/partnerAPI"><ObjectType xmlns="http://exacttarget.com/wsdl/partnerAPI">OpenEvent</ObjectType></RetrieveRequest>;
var propertiesArray = properties.split(",");
var payload = mc.getPayloadXML();

// appends properties elements to the RetrieveRequest element
for(var i = 0; i < propertiesArray.length; i++){
   listOpenEventsProperties.RetrieveRequest += <Properties xmlns="http://exacttarget.com/wsdl/partnerAPI">{propertiesArray[i]}</Properties>;
}

// Generates the xml Filter object 
if(openEventsFilter != null && openEventsFilter != ''){
   openEventsFilter = eval('(' + openEventsFilter + ')');
   listOpenEventsProperties.RetrieveRequest +=  <Filter xmlns="http://exacttarget.com/wsdl/partnerAPI" xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance" xsi:type="SimpleFilterPart">
                                                   {openEventsFilter.Property}
                                                   {openEventsFilter.SimpleOperator}
                                                   {openEventsFilter.Value}
                                                </Filter>;
}

payload.RetrieveRequestMsg = listOpenEventsProperties;
mc.setPayloadXML(payload);
