sequence.script:

var usernameFilter = mc.getProperty('uri.var.jira.usernameFilter');
var key = mc.getProperty('uri.var.jira.key');
var urlQuery = '';
if (usernameFilter != null && usernameFilter != ""){
   urlQuery += 'username=' + usernameFilter + '&';
}
if (key != null && key != ""){
   urlQuery += 'key=' + key + '&';
}
if (urlQuery != null && urlQuery != ""){
   urlQuery = '?' + urlQuery.substring(0, urlQuery.length - 1);
}
mc.setProperty('uri.var.jira.urlQuery',urlQuery);
