var xmlData="<Contacts> <row no=\"1\"> <FL val=\"PipelineDeals Person ID\">" + personId + "</FL> </row> </Contacts>";															
mc.setProperty('zohocrm.xmlData', xmlData);


//constructing the xml data in order to update the account in ZohoCrm								
