sequence.script:

var version = mc.getProperty('uri.var.version');
var language = mc.getProperty('uri.var.language');
var reservationId = mc.getProperty('uri.var.reservationId');
//setting required params for the url
var urlQuery = '?api_key=' + encodeURIComponent(mc.getProperty('uri.var.apiKey')) + '&method=get_invoice_items&reservation_id=' + reservationId;
//setting optional params for the url
if (version != null && version != '') {
   urlQuery += '&version=' + version;
}
 if (language != null && language != '') {
   urlQuery += '&language=' + language;
}
mc.setProperty('uri.var.urlQuery', urlQuery);
