var sortField=mc.getProperty("uri.var.sortField");
var sortDirection=mc.getProperty("uri.var.sortDirection");
if (page!=null && page!=""){
   urlQuery+='page=' + page +'&';
}
if (perPage!=null && perPage!=""){
   urlQuery+='per_page=' + perPage +'&';
}
if(query!=null && query!=""){
