sequence.script:

// optional url param variables
var length = mc.getProperty('uri.var.length');
var mark = mc.getProperty('uri.var.mark');
var since  = mc.getProperty('uri.var.since');

var uriParams = '';

if (length != null && length != "") {
	uriParams = 'length=' + length + '&';
}				
if (mark != null && mark != "") {
	uriParams += 'mark=' + encodeURIComponent(mark) + '&';
}				
if (since != null && since != "") {
	uriParams += 'since=' + encodeURIComponent(since) + '&';
}				

if(uriParams != ''){
	mc.setProperty('uri.var.uriParams' , uriParams.substring(0, uriParams.length-1));
}
