if (appliedFilter != null && appliedFilter != ""){
   urlQuery += 'applied_filter=' + appliedFilter + '&';
}

if (sortOrder != null && sortOrder != ""){
   urlQuery += 'sort_order=' + sortOrder + '&';
}

